import classes from "./Cart.module.css";
import Modal from "../../UI/Modal";
import nikeImg from "../../extra-data/nike-shoes.jpeg"
import tshirtsimage from "../../extra-data/nike-tshirts.jpeg"
import { useContext, useState } from "react";
import cartContext from "../../store/use-context";
import OrderDetails from "./OrderDetails";
import CheckOutDetails from "./CheckOutDetails";
import { Link, redirect, useActionData, useNavigation } from "react-router-dom";

const Cart = (props) => {
    const orderSent = useActionData();
    const { checkOut } = useContext(cartContext);
    const { hideCart } = props;

    return (
        <Modal onConfirm={hideCart}>
            {!checkOut && <OrderDetails hideCart={hideCart} />}
            {checkOut && <CheckOutDetails hideCart={hideCart} />}
        </Modal>
    )
};

export default Cart;
