import ProductItem from "./ProductItem";
import { useState, useEffect, useCallback, Suspense, Await, defer } from "react";
import { json, useLoaderData } from "react-router-dom";
import classes from "./PantsList.module.css"

const PantsList = () => {
    // const [shoesArray, setShoesArray] = useState([]);
    const { pants } = useLoaderData();
    // const transformData = useCallback(() => {
    //     let loadedShoes = [];
    //     for (const key in shoes) {
    //         loadedShoes.push({
    //             id: key,
    //             image: shoes[key].image,
    //             price: shoes[key].price,
    //             name: shoes[key].name
    //         });
    //     };
    //     setShoesArray(loadedShoes);
    // }, [shoes]);

    // useEffect(() => {
    //     transformData()
    // }, [transformData])
    return (
        <Suspense fallback={<p style={{ "textAlign": "center" }}>Loading Shoes...</p>}>
            <Await resolve={pants}>
                {(loadedItem) =>
                    <ul className={classes.ul}>
                        {Object.values(loadedItem).map(pant => (
                            <ProductItem
                                id={pant.id}
                                name={pant.name}
                                price={pant.price}
                                image={pant.image}
                            />
                        ))}</ul>
                }
            </Await>
        </Suspense>
    )
};

export default PantsList;

async function pantsLoader() {
    const request = await fetch(`https://players-c7f1b-default-rtdb.firebaseio.com/products/pants.json`);

    if (!request.ok) {
        throw json({ message: "Couldn't fetch the shoes" }, {
            status: 500
        })
    };

    const data = await request.json();
    return data
}
export async function loader() {
    return defer({
        pants: pantsLoader(),
    })
};
