import { Outlet, useLoaderData, redirect, json, useRouteLoaderData } from "react-router-dom";
import ProductsNavigation from "../components/ProductsNavigation";
import classes from "./ProductsPage.module.css"

const ProductsPage = () => {
    const { shoes, pants, tshirts } = useLoaderData();
    return <>
        <ProductsNavigation />
        <h3 className={classes.explore}>Explore our latest products</h3>
        <Outlet context={{ shoes, pants, tshirts }} />
    </>
};

export default ProductsPage;

export async function loader(id) {
    const request = await fetch(`https://players-c7f1b-default-rtdb.firebaseio.com/products.json`);

    if (!request.ok) {
        throw json({ message: "Couldn't fetch the shoes" }, {
            status: 500
        })
    };

    const data = await request.json();
    return data
};

