import { useCallback, useEffect, useState, Fragment, Suspense } from "react";
import { Await, json, useLoaderData } from "react-router-dom";
import ProductItem from "./ProductItem";
import classes from "./ShoesList.module.css"

const ShoesList = () => {
    const [shoesArray, setShoesArray] = useState([]);
    const data = useLoaderData();
    const transformData = useCallback(() => {
        let loadedShoes = [];
        for (const key in data) {
            loadedShoes.push({
                id: key,
                image: data[key].image,
                price: data[key].price,
                name: data[key].name
            });
        };
        setShoesArray(loadedShoes);
    }, [data]);

    useEffect(() => {
        transformData()
    }, [transformData])
    return (
            <Suspense fallback={<p style={{ "text-align": "center" }}>Loading...</p>}>
                <Await resolve={data}>
                    <ul className={classes.ul}>
                        {shoesArray.map(shoe => (
                            <ProductItem
                                id={shoe.id}
                                name={shoe.name}
                                price={shoe.price}
                                image={shoe.image}
                            />
                        ))}</ul>
                </Await>
            </Suspense>
    )
};

export default ShoesList;

export async function loader() {
    const request = await fetch(`https://players-c7f1b-default-rtdb.firebaseio.com/products/shoes.json`);

    if (!request.ok) {
        throw json({ message: "Couldn't fetch the shoes" }, {
            status: 500
        })
    };

    const data = await request.json();
    return data
};
