import { useContext, useState, useRef } from "react";
import cartContext from "../../store/use-context";

const CheckOutDetails = () => {
    const [cartDetails, setCartDetails] = useState("cash");
    const nameRef = useRef();
    const addressRef = useRef();
    const cartHolderRef = useRef();
    const cartNumberRef = useRef();
    const expiresRef = useRef();
    const codeRef = useRef();

    // const name = nameRef.current.value
    // const address = addressRef.current.value
    // const cartHolder = cartHolderRef.current.value
    // const cartNumber = cartNumberRef.current.value
    // const expires = expiresRef.current.value
    // const code = codeRef.current.value

    const { setCheckOut, cartItems } = useContext(cartContext)
    function paymentMethodHandler(event) {
        setCartDetails(event.target.value);
    }

    function editOrderHandler() {
        setCheckOut(false)
    }

    async function sendOrderToDataBase() {
        const response = await fetch("https://players-c7f1b-default-rtdb.firebaseio.com/products/order.json", {
            method: "POST",
            body: JSON.stringify([
                { order: cartItems.items, price: cartItems.totalPrice },
                // {
                //     name: name,
                //     address: address,
                //     paymentMethod: cartDetails,
                //     cartHolder: cartHolder ? cartHolder : null,
                //     cartNumber: cartNumber ? cartNumber : null,
                //     expires: expires ? expires : null,
                //     code: code ? code: null
                // }
            ]),
            headers: {
                "Content-Type": "application/json"
            }
        });

        if (!response.ok) {
            throw { message: "Order could not be sent" }
        }
    };

    function sendOrderHandler() {
        sendOrderToDataBase()
    }
    return (
        <Form>
            <div>
                <label htmlFor="name">Full name: </label>
                <input name="name" ref={nameRef} id="name" type="text" placeholder="Joe Doe..." required={true} />
            </div>
            <div>
                <label htmlFor="address">Address: </label>
                <input name="address" ref={addressRef} id="address" type="text" placeholder="Street name and number..." required={true} />
            </div>
            <div>
                <label htmlFor="payment">Payment: </label>
                <select name="payment-method" onChange={paymentMethodHandler} defaultValue="cash">
                    <option value="cash">Cash</option>
                    <option value="cart">Cart</option>
                </select>
                {cartDetails === "cart" &&
                    <div>
                        <div>
                            <label htmlFor="name">Cart Holder: </label>
                            <input name="cart-holder" ref={cartHolderRef} id="name" type="text" placeholder="Joe Doe..." required={true} />
                        </div>
                        <div>
                            <label htmlFor="cart-number">Cart Number: </label>
                            <input name="cart-number" ref={cartNumberRef} id="cart-number" type="text" placeholder="4719 7838 8973 3778" required={true} />
                        </div>
                        <div>
                            <label htmlFor="cart-expiry-date">Expires: </label>
                            <input name="cart-expiry-date" ref={expiresRef} id="cart-expiry-date" type="text" placeholder="03/26" required={true} />
                        </div>
                        <div>
                            <label htmlFor="cart-code">Code: </label>
                            <input name="cart-code" ref={codeRef} id="cart-code" type="password" placeholder="***" required={true} />
                        </div>
                    </div>}
            </div>
            <div>
                <button onClick={editOrderHandler}>Go Back</button>
                <button onClick={sendOrderHandler}>Finish order</button>
            </div>
        </Form>
    )
};

export default CheckOutDetails;
