import classes from "./ProductItem.module.css";
import nikeShoesImage from "../extra-data/nike-shoes.jpeg";
import nikeTshirtImage from "../extra-data/nike-tshirts.jpeg";
import nikePantsImage from "../extra-data/nike-pants.jpeg"
const ProductItem = ({ products }) => {
    return (
        <div className={classes.products}>
            <ul className={classes.list}>
                {products.length === 0 ?
                    <div className={classes.noProduct}>
                        <h3>No Products added</h3>
                    </div> : products.map((product) => (
                        <li key={product.id} className={classes.item}>
                            {/* <Link to={product.id}> */}
                            <img src={
                                product.image === "nike-shoes" && nikeShoesImage ||
                                product.image === "pantsimage" && nikePantsImage ||
                                product.image === "tshirtsimage" && nikeTshirtImage
                            } alt='shoes' />
                            <div className={classes.content}>
                                <h4>{product.name}</h4>
                                <p>Price: {product.price}$</p>
                            </div>
                            <div className={classes.actions}>
                                <div>
                                    <label htmlFor="quantity">Quantity: </label>
                                    <input type="number" name="quantity" id="quantity" defaultValue="1" min="1" />
                                </div>
                                <button>Add +</button>
                            </div>
                            {/* </Link> */}
                        </li>
                    ))}
            </ul>
        </div>
    )
};

export default ProductItem;
