import classes from "./Cart.module.css";
import Modal from "../../UI/Modal";
import nikeImg from "../../extra-data/nike-shoes.jpeg"
import useCart from "../../hooks/useCart";
import { useContext } from "react";
import cartContext from "../../store/use-context";

const Cart = (props) => {
    const ctx = useContext(cartContext);
    console.log(ctx.cartItems)
    const orderDetails = <div>
        <div className={classes.cartHeader}>
            <p>Your added to cart products</p>
            <div className={classes.labels}>
                <h3>Item</h3>
                <h3>Price</h3>
                <h3>Quantity</h3>
            </div>
        </div>

        <ul className={classes.list}>
            <li>
                <div className={classes.item}>
                    <img src={nikeImg} />
                    <h4>Nike</h4>
                </div>
                <h4>200$</h4>
                <h4>x2</h4>
                <div className={classes.actions}>
                    <button>-</button>
                    <button>+</button>
                </div>
            </li>
        </ul>
        <div>
            <h3>Total Price: 400$</h3>
            <div>
                <button>Back to shopping</button>
                <button>Proceed to checkout</button>
            </div>

        </div>
    </div>
    return (
        <Modal onConfirm={props.hideCart}>
            {orderDetails}
            {/* {!buttonClicked && <div>{orderDetails}</div>}
            {buttonClicked && <UserDetails onConfirm={props.onHideCart} onGoBack={onGoBackHandler} />} */}
        </Modal>
    )
};

export default Cart;
