import classes from "./Cart.module.css";
import Modal from "../../UI/Modal";
import nikeImg from "../../extra-data/nike-shoes.jpeg"
import tshirtsimage from "../../extra-data/nike-tshirts.jpeg"
import { useContext, useState } from "react";
import cartContext from "../../store/use-context";
import OrderDetails from "./OrderDetails";

const Cart = (props) => {
    const [cartDetails, setCartDetails] = useState("cash");
    const { hideCart } = props
    const { cartItems, checkOut, setCheckOut } = useContext(cartContext);


    // const orderDetails = <div>
    //     <div className={classes.cartHeader}>
    //         <p>Your products</p>
    //         <div className={classes.labels}>
    //             <h3>Item</h3>
    //             <h3>Price</h3>
    //             <h3>Quantity</h3>
    //         </div>
    //     </div>
    //     {cartItems.length === 0 && <p>No products added</p>}
    //     <ul className={classes.list}>
    //         {Object.values(cartItems.items).map(item => (
    //             <li key={item.id}>
    //                 <div className={classes.item}>
    //                     <img src={item.image} />
    //                     <h4>{item.name}</h4>
    //                 </div>
    //                 <h4>{item.price * item.amount}$</h4>
    //                 <h4>x{item.amount}</h4>
    //                 <div className={classes.actions}>
    //                     <button onClick={removeItemAmountHandler.bind(null, item.id)}>-</button>
    //                     <button onClick={addItemAmountHandler.bind(null, item)}>+</button>
    //                 </div>
    //             </li>
    //         ))}
    //     </ul>
    //     <div>
    //         <h3>Total Price: {cartItems.totalPrice}$</h3>
    //         <div>
    //             <button onClick={hideCart}>Back to shopping</button>
    //             <button onClick={proceedToCheckOutHandler}>Proceed to checkout</button>
    //         </div>

    //     </div>
    // </div>

    function paymentMethodHandler(event) {
        setCartDetails(event.target.value);
    }

    function editOrderHandler() {
        setCheckOut(false)
    }

    async function sendOrderToDataBase() {
        const response = await fetch("https://players-c7f1b-default-rtdb.firebaseio.com/products/order.json", {
            method: "POST",
            body: JSON.stringify(cartItems),
            headers: {
                "Content-Type": "application/json"
            }
        });

        if (!response.ok) {
            throw { message: "Order could not be sent" }
        }
    };

    function sendOrderHandler() {
        sendOrderToDataBase()
    }

    const checkOutDetails = <div>
        <div>
            <label htmlFor="name">Full name: </label>
            <input id="name" type="text" placeholder="Joe Doe..." required />
        </div>
        <div>
            <label htmlFor="address">Address: </label>
            <input id="address" type="text" placeholder="Street name and number..." required />
        </div>
        <div>
            <label htmlFor="payment">Payment: </label>
            <input id="paymeny" type="text" placeholder="Name..." required />
            <select onChange={paymentMethodHandler} defaultValue="cash">
                <option value="cash">Cash</option>
                <option value="cart">Card</option>
            </select>
            {cartDetails === "cart" &&
                <div>
                    <div>
                        <label htmlFor="name">Cart Holder: </label>
                        <input id="name" type="text" placeholder="Joe Doe..." required />
                    </div>
                    <div>
                        <label htmlFor="cart-number">Cart Number: </label>
                        <input id="cart-number" type="text" placeholder="4719 7838 8973 3778" required />
                    </div>
                    <div>
                        <label htmlFor="expires">Expires: </label>
                        <input id="expires" type="text" placeholder="03/26" required />
                    </div>
                    <div>
                        <label htmlFor="code">Code: </label>
                        <input id="code" type="password" placeholder="***" required />
                    </div>
                </div>}
        </div>
        <div>
            <button onClick={editOrderHandler}>Go Back</button>
            <button onClick={sendOrderHandler}>Finish order</button>
        </div>
    </div>
    return (
        <Modal onConfirm={hideCart}>
            {!checkOut && <OrderDetails />}
            {checkOut && checkOutDetails}
        </Modal>
    )
};

export default Cart;
