import { useReducer, useState } from "react";
const defaultCartState = {
    items: [],
    totalPrice: 0
};
const cartReducer = (state, action) => {
    if (action.type === "ADD") {
        const updatedTotalPrice = state.totalPrice + action.item.price * action.item.amount;
        const existingCartItemIndex = state.items.findIndex(item => item.id === action.item.id);
        const exisitingItem = state.items[existingCartItemIndex];
        console.log(exisitingItem)
        console.log(updatedTotalPrice)
        console.log(action.item)
    }
    return defaultCartState
}

const useCart = () => {
    const [cartItems, setCartItems] = useReducer(cartReducer, defaultCartState);
    const addItem = item => {
        setCartItems({ type: "ADD", item: item })
    };

    const removeItem = id => {
        console.log(id)
    }

    return { addItem, removeItem, cartItems }
};

export default useCart;
