import ProductItem from "../ProductItem";
import nikePantsImage from "../../extra-data/nike-pants.jpeg"
import { useState, useEffect, useCallback, Suspense } from "react";
import { Await, defer, json, useLoaderData, useOutlet, useOutletContext } from "react-router-dom";

const PantsList = () => {
    const [pantsArray, setPantsArray] = useState([]);
    const data = useLoaderData();
    // const transformData = useCallback(() => {
    //     let loadedShoes = [];
    //     for (const key in data) {
    //         loadedShoes.push({
    //             id: key,
    //             image: data[key].image,
    //             price: data[key].price
    //         })
    //     }
    //     setPantsArray(loadedShoes);
    // }, [data]);

    // useEffect(() => {
    //     transformData()
    // }, [transformData])


    return (
        <>
            <Suspense fallback={<p>Loading...</p>}>
                <Await resolve={data}>
                    {(loadedPants) => <ProductItem products={loadedPants} />}
                    {/* <ProductItem products={pantsArray} /> */}
                </Await>
            </Suspense>
        </>


    )
};

export default PantsList;

async function pantsLoader() {
    const request = await fetch(`https://players-c7f1b-default-rtdb.firebaseio.com/products/pants.json`);

    if (!request.ok) {
        throw json({ message: "Couldn't fetch the shoes" }, {
            status: 500
        })
    };

    const data = await request.json();
    return data
};

export async function loader() {
    defer({
        pants: loader()
    })
};
