import { useRouteLoaderData } from "react-router-dom";
import classes from "./Welcome.module.css"
import cartimg from "../extra-data/cart.svg"
import { useState } from "react";
import Cart from "./Cart/Cart";
const Welcome = () => {
    const [cartIsShown, setCartIsShown] = useState(false);
    const showCartHandler = () => {
        setCartIsShown(true);
    };

    const hideCartHandler = () => {
        setCartIsShown(false);
    }

    const cartButton = <>{
        !cartIsShown && <div className={classes.cartDiv}>
            <img className={classes.cart} src={cartimg} />
            <button onClick={showCartHandler}>Cart<span> 0</span></button></div>
    }</>
    const token = useRouteLoaderData("root");
    return (
        <div className={classes.welcome}>
            {token && <>
                <h4>Welcome</h4>
                {cartButton}
            </>}
            {cartIsShown && <Cart hideCart={hideCartHandler} />}
        </div>

    )
};

export default Welcome;
