import { Outlet, NavLink, useLocation } from "react-router-dom";
import ProductsNavigation from "../components/ProductsNavigation";
import classes from "./ProductsPage.module.css"
import shoesLink from "../extra-data/shoesLink.jpeg"
import pantsLink from "../extra-data/pantsLink.jpeg"
import tshirtsLink from "../extra-data/tshirtsLink.jpeg"
import { useEffect, useState } from "react";

const ProductsPage = () => {
    const [urlLink, setUrlLink] = useState(false);
    const location = useLocation();
    useEffect(() => {}, [urlLink])
    return <>
        <ProductsNavigation />
        <h3 className={classes.explore}>Explore our latest products</h3>
        {location.pathname === "products" && <div className={classes.navigation}>
            <NavLink to="/products/shoes" className={classes.navLinks}>
                <img src={shoesLink} />
            </NavLink>
            <NavLink to="/products/tshirts" className={classes.navLinks}>
                <img src={tshirtsLink} />
            </NavLink>
            <NavLink to="/products/pants" className={classes.navLinks}>
                <img src={pantsLink} />
            </NavLink>
        </div>}
        <Outlet />
    </>
};

export default ProductsPage;

