import { useReducer } from "react";
import cartContext from "./use-context";

const defaultState = {
    items: [],
    totalPrice: 0
};

const cartReducer = (state, action) => {
    return defaultState
}

const ContextProvider = () => {
    const [cart, dispatchCart] = useReducer(cartReducer, defaultState);

    const addToCart = product => {
        dispatchCart({ action: "ADD", product })
    }

    const cartCxt = {
        cartItems: [],
        addToCart: addToCart,
        removeFromCart: () => { }
    }
    return (
        <cartContext.Provider value={cartCxt}></cartContext.Provider>
    )
}

export default ContextProvider;
