import { Outlet, useLoaderData, useLocation, useNavigate, useNavigation, json, defer } from "react-router-dom";
import ProductsNavigation from "../components/ProductsNavigation";
import classes from "./ProductsPage.module.css"

const ProductsPage = () => {
    const location = useLocation();
    let productName = location.pathname.substr(10);
    const {shoes, pants, tshirts} = useLoaderData();
    return <>
        <ProductsNavigation />
        <h3 className={classes.explore}>Explore our latest {productName ? productName : "products"}</h3>
        <Outlet context={{name: "name"}} />
    </>
};

export default ProductsPage;

async function product(id) {
    const request = await fetch(`https://players-c7f1b-default-rtdb.firebaseio.com/products/${id}.json`);

    if (!request.ok) {
        throw json({ message: "Couldn't fetch the shoes" }, {
            status: 500
        })
    };

    const data = await request.json();
    return data
};

export const loader = async () => {
    return defer({
        shoes: await product(shoes),
        pants: await product(pants),
        tshirts: await product(tshirt)
    });
}
