import { useReducer } from "react";
import cartContext from "./use-context";

const defaultCartState = {
    items: [],
    totalPrice: 0
};

const cartReducer = (state, action) => {
    if (action.type === "ADD") {
        const updatedTotalAmount =
          state.totalAmount + action.item.price * action.item.amount;

        const existingCartItemIndex = state.items.findIndex(
          (item) => item.id === action.item.id
        );
        const exisitingCartItem = state.items[existingCartItemIndex];
        let updatedItems;

        if (exisitingCartItem) {
          const updatedItem = {
            ...exisitingCartItem,
            amount: exisitingCartItem.amount + action.item.amount,
          };
          updatedItems = [...state.items];
          updatedItems[existingCartItemIndex] = updatedItem;
        } else {
          updatedItems = state.items.concat(action.item); // concat generates a brand new object
        }

        return {
          items: updatedItems,
          totalAmount: updatedTotalAmount,
        };
      }
    return defaultCartState
}

const ContextProvider = (props) => {
    const [cart, dispatchCart] = useReducer(cartReducer, defaultCartState);

    const addToCart = product => {
        dispatchCart({ action: "ADD", product })
    }

    const removeFromCart = id => {
        dispatchCart({action: "REMOVE", id})
    }

    const cartCxt = {
        cartItems: cart,
        addToCart: addToCart,
        removeFromCart: removeFromCart
    }
    return (
        <cartContext.Provider value={cartCxt}>
            {props.children}
        </cartContext.Provider>
    )
}

export default ContextProvider;
