import { useCallback, useEffect, useState } from "react";
import { json, useLoaderData, useOutletContext } from "react-router-dom";
import ProductItem from "../ProductItem";

const ShoesList = (props) => {
    const {shoes} = useOutletContext();
    const [shoesArray, setShoesArray] = useState([]);
    const transformData = useCallback(() => {
        let loadedShoes = [];
        for (const key in shoes) {
            loadedShoes.push({
                id: key,
                image: shoes[key].image,
                price: shoes[key].price
            });
        };
        setShoesArray(loadedShoes);
    }, [shoes]);

    useEffect(() => {
        transformData()
    }, [transformData])
    return (
        <ProductItem products={shoesArray} />
    )
};

export default ShoesList;
