import { useContext, useState } from "react";
import cartContext from "../../store/use-context";
import { Form } from "react-router-dom";

const CheckOutDetails = () => {
    const [paymentMethod, setPaymentMethod] = useState("cash");

    const { setCheckOut, cartItems } = useContext(cartContext)
    function paymentMethodHandler(event) {
        setPaymentMethod(event.target.value);
    }

    function editOrderHandler() {
        setCheckOut(false)
    }

    async function sendOrderToDataBase() {
        const response = await fetch("https://players-c7f1b-default-rtdb.firebaseio.com/products/order.json", {
            method: "POST",
            body: JSON.stringify([
                { order: cartItems.items, price: cartItems.totalPrice },
            ]),
            headers: {
                "Content-Type": "application/json"
            }
        });

        if (!response.ok) {
            throw { message: "Order could not be sent" }
        }
    };

    function sendOrderHandler() {
        sendOrderToDataBase()
    }
    return (
        <Form>
            <div>
                <label htmlFor="name">Full name: </label>
                <input name="name" id="name" type="text" placeholder="Joe Doe..." required />
            </div>
            <div>
                <label htmlFor="address">Address: </label>
                <input name="address" id="address" type="text" placeholder="Street name and number..." required />
            </div>
            <div>
                <label htmlFor="payment">Payment: </label>
                <select value={paymentMethod} name="payment-method" onChange={paymentMethodHandler} defaultValue="cash">
                    <option value="cash">Cash</option>
                    <option value="cart">Cart</option>
                </select>
                {paymentMethod === "cart" &&
                    <div>
                        <div>
                            <label htmlFor="name">Cart Holder: </label>
                            <input name="cart-holder" id="name" type="text" placeholder="Joe Doe..." required />
                        </div>
                        <div>
                            <label htmlFor="cart-number">Cart Number: </label>
                            <input name="cart-number" id="cart-number" type="text" placeholder="4719 7838 8973 3778" required />
                        </div>
                        <div>
                            <label htmlFor="cart-expiry-date">Expires: </label>
                            <input name="cart-expiry-date" id="cart-expiry-date" type="text" placeholder="03/26" required />
                        </div>
                        <div>
                            <label htmlFor="cart-code">Code: </label>
                            <input name="cart-code" id="cart-code" type="password" placeholder="***" required />
                        </div>
                    </div>}
            </div>
            <div>
                <button onClick={editOrderHandler}>Go Back</button>
                <button onClick={sendOrderHandler}>Finish order</button>
            </div>
        </Form>
    )
};

export default CheckOutDetails;
