import { Form, Link, useActionData, useNavigate, useSearchParams, redirect, json, useLoaderData } from "react-router-dom";
import Button from "../UI/Button";
import classes from "./AuthPage.module.css"

const AuthPage = () => {
    const [searchParams] = useSearchParams();
    const login = searchParams.get("mode") === "login";
    const actionData = useActionData();
    const navigate = useNavigate();
    // const userData = useLoaderData();
    const signInHandler = event => {
        event.preventDefault();
        return
        // navigate("/products");
    };

    return <>
        <Form method="post" className={classes.form}>
            {/* {!login &&
                <>
                    <div>
                        <label htmlFor="name">Name*</label>
                        <input id="namr" name="name" placeholder="Name" />
                    </div>
                    <div>
                        <label htmlFor="surname">Surname*</label>
                        <input id="surname" name="surname" placeholder="Surname" />
                    </div>
                </>} */}
            {login ? <p>Login</p> : <p>Create new user</p>}
            <div>
                <label htmlFor="email">Email</label>
                <input id="email" name="email" placeholder="Email-address" />
            </div>
            <div>
                <label htmlFor="password">Password</label>
                <input id="password" name="password" placeholder="Password" />
            </div>
            <div className={classes.actions}>
                <Link to={`?mode=${login ? "signup" : "login"}`}>{login ? "SignUp" : "Login"}</Link>
                <button onSubmit={signInHandler}>Save</button>
            </div>
        </Form>
    </>
};

export default AuthPage;

export async function loader({ request, params }) {
    const response = await fetch("https://players-c7f1b-default-rtdb.firebaseio.com/nikeusers.json");
    if (!response.ok) {
        throw json({ message: "Couldn't receive the user information" }, { status: 500 })
    };
    const userData = await response.json();
    return userData;
}

export const action = async ({request, params}) => {
    const searchParams = new URL(request.url).searchParams;
    const mode = searchParams.get("mode") || "signup";
    const formData = await request.formData();
    const authData = {
      email: formData.get("email"),
      password: formData.get("password")
    };

    const response = await fetch("http://localhost:8080/" + mode, {
      headers: {
        "Content-Type": "application/json"
      },
      method: "POST",
      body: JSON.stringify(authData)
    });

    if(response.status === 422 || response.status === 421) {
      return response
    };

    if(!response.ok) {
      throw json({message: "Something went wrong"}, {status: 500})
    };

    return redirect("/products")
  }
