import classes from "./Cart.module.css";
import Modal from "../../UI/Modal";
import nikeImg from "../../extra-data/nike-shoes.jpeg"
import tshirtsimage from "../../extra-data/nike-tshirts.jpeg"
import { useContext, useState } from "react";
import cartContext from "../../store/use-context";
import OrderDetails from "./OrderDetails";
import CheckOutDetails from "./CheckOutDetails";
import { Link, redirect, useActionData, useNavigation } from "react-router-dom";

const Cart = (props) => {
    const isSubmitted = useActionData();
    const { checkOut } = useContext(cartContext);

    return (
        <Modal>
            {!checkOut && !isSubmitted && <OrderDetails />}
            {checkOut && !isSubmitted && <CheckOutDetails />}
            {isSubmitted && <>
                <p>Order sent successfully</p>
                <Link to="/products">Back to shopping</Link>
            </>}
        </Modal>
    )
};

export default Cart;

export async function action({ request, params }) {
    let orderSent = false;
    const formData = await request.formData();
    const orderData = {
        name: formData.get("name"),
        address: formData.get("address"),
    }
    const response = await fetch("https://players-c7f1b-default-rtdb.firebaseio.com/products/order.json", {
        method: request.method,
        body: JSON.stringify(orderData),
        headers: {
            "Content-Type": "application/json"
        }
    });

    if (!response.ok) {
        throw { message: "Order could not be sent" }
    }

    orderSent = true;
    return isSubmitted
}
