import { Form, Link, useActionData, useSearchParams, redirect, json, useNavigation } from "react-router-dom";
import classes from "./AuthPage.module.css"

const AuthPage = () => {
    const [searchParams] = useSearchParams();
    const isSignUp = searchParams.get("mode") === "signup";
    const navigation = useNavigation();
    const submitting = navigation.state === "submitting"
    const actionData = useActionData();

    const authHandler = event => {
        event.preventDefault();
        return
        // navigate("/products");
    };
    let buttonName;
    if (isSignUp) {
        buttonName = "Sign Up"
    } else {
        buttonName = "Log in"
    }

    return <>
        <Form method="post" className={classes.form}>
            {!isSignUp ? <p>Please give in your credentials</p> : <p>Create a new user</p>}
            {/* {isSignUp &&
                <>
                    <div>
                        <label htmlFor="name">Name*</label>
                        <input id="namr" name="name" placeholder="Name" />
                    </div>
                    <div>
                        <label htmlFor="surname">Surname*</label>
                        <input id="surname" name="surname" placeholder="Surname" />
                    </div>
                </>} */}
            {actionData && actionData.errors && <ul className={classes.errors}>{Object.values(actionData.errors).map(err => <li key={err}>{err}</li>)}</ul>}
            {actionData && <p>{actionData.message}</p>}
            <div>
                <label htmlFor="email">Email</label>
                <input id="email" name="email" placeholder="Email-address" />
            </div>
            <div>
                <label htmlFor="password">Password</label>
                <input id="password" name="password" placeholder="Password" />
            </div>
            <section className={classes.actions}>
                <Link className={classes.link} to={`?mode=${isSignUp ? "login" : "signup"}`}>{isSignUp ? "Log in" : "Sign Up"}</Link>
                <button disabled={submitting} onSubmit={authHandler}>{submitting ? "Submitting" : `${buttonName}`}</button>
            </section>
        </Form>
    </>
};

export default AuthPage;

export const action = async ({ request, params }) => {
    const searchParams = new URL(request.url).searchParams;
    const mode = searchParams.get("mode") || "login";
    const formData = await request.formData();
    const authData = {
        email: formData.get("email"),
        password: formData.get("password")
    };

    const response = await fetch("http://localhost:8080/" + mode, {
        headers: {
            "Content-Type": "application/json"
        },
        method: "POST",
        body: JSON.stringify(authData)
    });

    if (response.status === 422 || response.status === 421) {
        return response
    };

    if (!response.ok) {
        throw json({ message: "Something went wrong" }, { status: 500 })
    };

    const resData = await response.json();
    console.log(resData);
    const token = resData.token;
    localStorage.setItem("token", token);

    return redirect("/products")
}
