import { useReducer } from "react"

const inputReducer = (state, action) => {
    if(action.type === "USER") {
      return {enteredInputValue: action.value, isTouched: false}
    }
    if(action.type === "USER_BLUR") {
      return {enteredInputValue: state.enteredInputValue , isTouched: action.touched}
    }
    return { enteredInputValue: "", isTouched: false };
  };

  const UseInput = (validateValue) => {

    const [input, dispatchInput] = useReducer(inputReducer, {
      enteredInputValue: "",
      isTouched: false,
    });

    const inputIsValid = validateValue(input.enteredInputValue);
    const inputHasError = !inputIsValid && input.isTouched;

    const inputChangeHandler = (event) => {
      dispatchInput({type: "USER", value: event.target.value});
    };

    const focusInputHandler = () => {
      dispatchInput({type: "USER_BLUR", touched: true});
    };

    const resetInput = () => {
      dispatchInput({type: "USER", value: ""});
      dispatchInput({type: "USER_BLUR", touched: false});
    };

    return {
      value: input.enteredInputValue,
      inputIsValid,
      resetInput,
      inputHasError,
      inputChangeHandler,
      focusInputHandler,
    };
  };

  export default UseInput;
