import { useState, useEffect, useCallback, Fragment } from "react";
import { json, useLoaderData } from "react-router-dom";
import ProductItem from "../ProductItem";

const TshirtsList = () => {
    const [tshirtsArray, setTshirtsArray] = useState([]);
    const data = useLoaderData();
    const transformData = useCallback(() => {
        let loadedShoes = [];
        for (const key in data) {
            loadedShoes.push({
                id: key,
                image: data[key].image,
                price: data[key].price
            })
        }
        setTshirtsArray(loadedShoes);
    }, [data]);

    useEffect(() => {
        transformData()
    }, [transformData]);

    return (
        <Fragment>
             <ul className={classes.ui}>
            {tshirtsArray.map(tshirt => (
                <ProductItem products={tshirtsArray}
                    id={tshirt.id}
                    name={tshirt.name}
                    price={tshirt.price}
                    image={tshirt.image}
                />
            ))}</ul>
        </Fragment>
    )
};

export default TshirtsList;

export async function loader() {
    const request = await fetch(`https://players-c7f1b-default-rtdb.firebaseio.com/products/tshirts.json`);

    if (!request.ok) {
        throw json({ message: "Couldn't fetch the shoes" }, {
            status: 500
        })
    };

    const data = await request.json();
    return data
};
